pc.script.createLoadingScreen(function (app) {
    // --- Create full-screen wrapper ---
    const wrapper = document.createElement('div');
    Object.assign(wrapper.style, {
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        backgroundColor: '#000', // Background color
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 9999
    });
    document.body.appendChild(wrapper);

    // --- Create logo image ---
    const logoImg = document.createElement('img');
    // ðŸ‘‡ Your FlappyBird logo embedded directly (Base64, no loading delay)
    logoImg.src = "data:image/png;base64,"

    Object.assign(logoImg.style, {
        width: '300px',
        marginBottom: '30px',
        filter: 'drop-shadow(0 0 10px rgba(255,255,255,0.3))'
    });
    wrapper.appendChild(logoImg);

    // --- Create progress bar container ---
    const barContainer = document.createElement('div');
    Object.assign(barContainer.style, {
        width: '60%',
        maxWidth: '400px',
        height: '10px',
        backgroundColor: 'rgba(255,255,255,0.15)',
        borderRadius: '5px',
        overflow: 'hidden',
        position: 'relative'
    });
    wrapper.appendChild(barContainer);

    // --- Create main progress bar ---
    const bar = document.createElement('div');
    Object.assign(bar.style, {
        width: '0%',
        height: '100%',
        background: 'linear-gradient(90deg, #00e6ff, #008cff)',
        boxShadow: '0 0 12px rgba(0, 255, 255, 0.5)',
        transition: 'width 0.2s ease'
    });
    barContainer.appendChild(bar);

    // --- Add animated shine effect ---
    const shine = document.createElement('div');
    Object.assign(shine.style, {
        position: 'absolute',
        top: 0,
        left: '-50%',
        width: '50%',
        height: '100%',
        background: 'linear-gradient(120deg, transparent, rgba(255,255,255,0.5), transparent)',
        animation: 'shineMove 2s infinite linear'
    });
    barContainer.appendChild(shine);

    // --- Add keyframes for shine animation ---
    const style = document.createElement('style');
    style.innerHTML = `
        @keyframes shineMove {
            0% { left: -50%; }
            100% { left: 100%; }
        }
    `;
    document.head.appendChild(style);

    // --- Update progress bar as game loads ---
    app.on('preload:progress', function (progress) {
        bar.style.width = `${Math.min(1, Math.max(0, progress)) * 100}%`;
    });

    // --- Fade out once everything is loaded ---
    app.on('preload:end', function () {
        wrapper.style.transition = 'opacity 0.6s ease';
        wrapper.style.opacity = '0';
        setTimeout(() => wrapper.remove(), 600);
    });
});
